<?php include_once 'config.php'; $data = json_decode(file_get_contents("php://input")); if (isset($data->name) && isset($data->email) && isset($data->message)) { try { $stmt = $conn->prepare("INSERT INTO contact_messages (name, email, subject, message) VALUES (?, ?, ?, ?)"); $stmt->execute([$data->name, $data->email, $data->subject, $data->message]); echo json_encode(["message" => "Message sent successfully"]); } catch (Exception $e) { http_response_code(500); echo json_encode(["error" => $e->getMessage()]); } } ?>