<?php require 'config.php'; header('Content-Type: application/json'); try { $data = []; $data['quotes'] = $conn->query("SELECT * FROM quotes ORDER BY created_at DESC")->fetchAll(PDO::FETCH_ASSOC); $data['notifications'] = $conn->query("SELECT * FROM notifications ORDER BY created_at DESC")->fetchAll(PDO::FETCH_ASSOC); $data['flashcards'] = $conn->query("SELECT * FROM flashcards")->fetchAll(PDO::FETCH_ASSOC); $data['hacks'] = $conn->query("SELECT * FROM memory_hacks")->fetchAll(PDO::FETCH_ASSOC); $data['blogPosts'] = $conn->query("SELECT id, title, excerpt, content, author, category, image_url as imageUrl, created_at as date FROM blog_posts ORDER BY created_at DESC")->fetchAll(PDO::FETCH_ASSOC); $tests = $conn->query("SELECT * FROM tests")->fetchAll(PDO::FETCH_ASSOC); foreach ($tests as &$test) { $stmt = $conn->prepare("SELECT q.*, tq.question_order FROM questions q JOIN test_questions tq ON q.id = tq.question_id WHERE tq.test_id = :tid ORDER BY tq.question_order ASC"); $stmt->execute([':tid' => $test['id']]); $qs = $stmt->fetchAll(PDO::FETCH_ASSOC); foreach ($qs as &$q) { $q['options'] = json_decode($q['options_json']); } $test['questions'] = $qs; } $data['tests'] = $tests; echo json_encode($data); } catch(PDOException $e) { http_response_code(500); echo json_encode(["error" => "Fetch failed", "message" => $e->getMessage()]); }