<?php require 'config.php'; header('Content-Type: application/json'); $data=json_decode(file_get_contents("php://input")); if(!isset($data->email)){exit();} $email=$data->email; $password=$data->password; if($email==='admin' && $password==='Ishika@123'){ $u=$conn->query("SELECT * FROM users WHERE email='admin'")->fetch(PDO::FETCH_ASSOC); if($u){ $u['role']='ADMIN'; echo json_encode(["message"=>"Success","user"=>$u]); exit(); }} try{ $stmt=$conn->prepare("SELECT * FROM users WHERE email=:e"); $stmt->execute([':e'=>$email]); $user=$stmt->fetch(PDO::FETCH_ASSOC); if($user && password_verify($password,$user['password_hash'])){ echo json_encode(["message"=>"Success","user"=>$user]); }else{ http_response_code(401); echo json_encode(["message"=>"Invalid"]); } }catch(Exception $e){ echo json_encode(["error"=>$e->getMessage()]); }