<?php require 'config.php'; header('Content-Type: application/json'); $data=json_decode(file_get_contents("php://input")); $action = isset($data->action) ? $data->action : (isset($_GET['action']) ? $_GET['action'] : ''); if($action == 'send_notification'){ $conn->prepare("INSERT INTO notifications (id, title, message, type) VALUES (?,?,?,?)")->execute([$data->id, $data->title, $data->message, $data->type]); } if($action == 'add_quote'){ $conn->prepare("INSERT INTO quotes (id, text, author) VALUES (?,?,?)")->execute([$data->id, $data->text, $data->author]); } if($action == 'delete_quote'){ $conn->prepare("DELETE FROM quotes WHERE id=?")->execute([$_GET['id']]); } echo json_encode(["message"=>"Success"]);