<?php require 'config.php'; header('Content-Type: application/json'); $d=json_decode(file_get_contents("php://input")); if($d->action=='send_notification'){ $conn->prepare("INSERT INTO notifications (id,title,message,type) VALUES (?,?,?,?)")->execute([$d->id, $d->title, $d->message, $d->type]); } if($d->action=='add_quote'){ $conn->prepare("INSERT INTO quotes (id,text,author) VALUES (?,?,?)")->execute([$d->id, $d->text, $d->author]); } if($_GET['action']=='delete_quote'){ $id=$_GET['id']; $conn->query("DELETE FROM quotes WHERE id='$id'"); } echo json_encode(["message"=>"OK"]);