<?php require 'config.php'; header('Content-Type: application/json'); $method = $_SERVER['REQUEST_METHOD']; if($method == 'GET') { echo json_encode($conn->query("SELECT * FROM contact_messages ORDER BY created_at DESC")->fetchAll(PDO::FETCH_ASSOC)); } if($method == 'DELETE') { $id = $_GET['id']; $conn->prepare("DELETE FROM contact_messages WHERE id=?")->execute([$id]); echo json_encode(["message"=>"Deleted"]); }