<?php require 'config.php'; header('Content-Type: application/json'); $d=json_decode(file_get_contents("php://input")); if($d->action=='add_question'){ $opts=json_encode($d->options); $conn->prepare("INSERT INTO questions (id,subject_id,topic_id,question_text,options_json,correct_option_index) VALUES (?,?,?,?,?,?)")->execute([$d->id, $d->subjectId, $d->topicId, $d->text, $opts, $d->correctOptionIndex]); } if($d->action=='create_test'){ $conn->prepare("INSERT INTO tests (id,title,duration_minutes,category,difficulty,exam_type) VALUES (?,?,?,?,?,?)")->execute([$d->id, $d->title, $d->durationMinutes, 'ADMIN', $d->difficulty, $d->examType]); $i=0; foreach($d->questions as $q){ $conn->prepare("INSERT INTO test_questions (test_id,question_id,question_order) VALUES (?,?,?)")->execute([$d->id, $q.id, $i++]); } } echo json_encode(["message"=>"OK"]);