<?php require 'config.php'; header('Content-Type: application/json'); $m=$_SERVER['REQUEST_METHOD']; $d=json_decode(file_get_contents("php://input")); if($m=='PUT'){ $iv=$d->isVerified?1:0; $conn->prepare("UPDATE users SET full_name=?, target_exam=?, institute=?, is_verified=$iv WHERE id=?")->execute([$d->name, $d->targetExam, $d->institute, $d->id]); } if($m=='DELETE'){ $id=$_GET['id']; $conn->query("DELETE FROM users WHERE id=$id"); } echo json_encode(["message"=>"OK"]);