<?php require 'config.php'; header('Content-Type: application/json'); $d=json_decode(file_get_contents("php://input")); $uid=$d->user_id; $conf=json_encode($d->config); $slots=json_encode($d->slots); $conn->query("DELETE FROM timetable_settings WHERE user_id=$uid"); $conn->prepare("INSERT INTO timetable_settings (user_id, config_json, generated_slots_json) VALUES (?,?,?)")->execute([$uid, $conf, $slots]); echo json_encode(["message"=>"Saved"]);