<?php require 'config.php'; header('Content-Type: application/json'); error_reporting(0); try { $tables = []; $stmt = $conn->query("SHOW TABLES"); while ($row = $stmt->fetch(PDO::FETCH_NUM)) { $tb = $row[0]; $count = $conn->query("SELECT COUNT(*) FROM $tb")->fetchColumn(); $tables[] = ["name" => $tb, "rows" => $count]; } echo json_encode(["status" => "CONNECTED", "server_info" => $conn->getAttribute(PDO::ATTR_SERVER_INFO), "tables" => $tables]); } catch (Exception $e) { echo json_encode(["status" => "ERROR", "message" => $e->getMessage()]); }