<?php require 'config.php'; header('Content-Type: application/json'); $d=json_decode(file_get_contents("php://input")); $url=$d->url; if(strpos($url, 'youtube.com/embed/') === false) { echo json_encode(["status"=>"invalid", "reason"=>"format"]); exit; } $headers = @get_headers($url); if($headers && strpos($headers[0], '200')) { echo json_encode(["status"=>"valid"]); } else { echo json_encode(["status"=>"invalid", "reason"=>"404/Block"]); }